import { useEffect, useState } from "react";

const Priorities = () => {
  const [weights, setWeights] = useState({
    academic_weight: 1,
    wellbeing_weight: 1,
    friends_weight: 1,
    disrespect_weight: 1,
    activities_weight: 1,
  });

  const [status, setStatus] = useState("");

  useEffect(() => {
    const fetchLatest = async () => {
      try {
        const res = await fetch("http://localhost:8000/priorities/latest");
        if (res.ok) {
          const data = await res.json();
          setWeights(data);
        }
      } catch (err) {
        console.log("No saved priorities yet.");
      }
    };

    fetchLatest();
  }, []);

  const handleChange = (key, value) => {
    setWeights({ ...weights, [key]: parseFloat(value) });
  };

  const handleSubmit = async () => {
    try {
      const res = await fetch("http://localhost:8000/priorities", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(weights),
      });

      if (res.ok) {
        setStatus("✅ Priorities saved.");
      } else {
        setStatus("❌ Failed to save.");
      }
    } catch (err) {
      setStatus("❌ Error saving priorities.");
    }

    setTimeout(() => setStatus(""), 3000);
  };

  return (
    <div className="p-6 max-w-3xl mx-auto bg-white rounded shadow">
      <h1 className="text-2xl font-bold mb-4 text-blue-800">⚙️ Prioritization Settings</h1>

      {Object.entries(weights).map(([key, value]) => (
        <div key={key} className="mb-6">
          <label className="block mb-1 font-medium capitalize text-gray-700">
            {key.replace("_weight", "").replace("_", " ")}
          </label>
          <input
            type="range"
            min={0}
            max={5}
            step={0.1}
            value={value}
            onChange={(e) => handleChange(key, e.target.value)}
            className="w-full"
          />
          <p className="text-sm text-gray-500">Weight: {value}</p>
        </div>
      ))}

      <button
        onClick={handleSubmit}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Save Priorities
      </button>

      {status && <p className="mt-3 text-green-600 font-medium">{status}</p>}
    </div>
  );
};

export default Priorities;
